    private Task loadTask(String id, String name, String description)
    {
        boolean completed = Boolean.TRUE.equals(configManager.getConfiguration("ironmanplanner", "task." + id, Boolean.class));
        return new Task(id, name, description, completed);
    }
    public void toggleTask(Task task)
    {
        task.setCompleted(!task.isCompleted());
        configManager.setConfiguration("ironmanplanner", "task." + task.getId(), task.isCompleted());
    }


